import React, { Component } from 'react'
import axios from 'axios';



class App extends Component {
  constructor(props) {
    super(props);

    this.state = {

      bookings:[]

    };
  }
componentDidMount(){


this.retrieveBookings();
}


retrieveBookings(){
  axios.get("http://localhost:8015/booking").then(res=>{

  if(res.data.success){
    this.setState({
      bookings:res.data.existingBookings
    });
    console.log(this.state.bookings);
  }
  });
}





  render() {
    return (
      <div>
        {this.state.bookings.map(booking=>(

          <div>
            <p> All Booking Details</p>
            <table class ="table">
            <thead>

              <tr>
                <th scope="col">#</th>
                <th scope="col">Customer Name</th>
                <th scope="col">Customer Email</th>
                <th scope="col">Mobile Number</th>
                <th scope="col">Tour Option </th>
                <th scope="col">Date</th>
                <th scope="col">Time</th>
                <th scope="col">Tour Guide</th>
                <th scope="col">Action</th>




              </tr>
            </thead>
              <tbody>
                {this.state.bookings.map((bookings,index) =>{

                    <tr>
                      <th scope="row">{index+1}</th>
                      <td>{booking.CustomerName}</td>
                      <td>{bookings.CustomerEmail}</td>
                      <td>{bookings.MobileNumber}</td>
                      <td>{bookings.TourOption}</td>
                      <td>{bookings.Date}</td>
                      <td>{bookings.Time}</td>
                      <td>{bookings.TourGuideName}</td>
                      <td>
                        <a className="btn btn-warning" href ="#">
                        <i className ="fas fa-edit"> </i>&nbsp;  Edit
                        </a>
                        &nbsp;
                        <a className="btn btn-danger" href ="#">
                        <i className ="far fa-trash"> </i>&nbsp;  Delete
                        </a>

                      </td>
                    </tr>
                })}
              </tbody>





</table>






          </div>

        ))}


      </div>
    )
  }
}

export default App
