import Select from 'react-select'

export default class CreateAnimal extends Component{

    constructor(props){
        super(props);
        this.state={
            Animal_ID:"",
            Animal_Name:"",
            Animal_Species:"",
            Animal_Date_Of_Birth:"",
            Animal_Gender:"",
            Feeding_And_Watering_Date:"",
            Feeding_And_Watering_Time:"",
            Animal_Satisfaction_Level:"",
