/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Component } from 'react'
import axios from 'axios';
import '../CSS/'


class TourGuideDashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {

      bookings:[]
      
    };
  }
componentDidMount(){


this.retrieveBookings();
}


retrieveBookings(){
  axios.get("http://localhost:8015/booking").then(res=>{

  if(res.data.success){
    this.setState({
      bookings:res.data.existingBookings
    });
      
  }
  });
}
onDelete = (id)=>{

  axios.delete(`http://localhost:8015/booking/delete/${id}`).then((res)=>{

    alert("Deleted Successfully");
    this.retrieveBookings();
  })

}

filterData(bookings,searchKey){
  const result =bookings.filter((booking)=>
  booking.CustomerEmail.toLowerCase().includes(searchKey) ||
  booking.CustomerName.toLowerCase().includes(searchKey)  ||
  booking.TourGuideName.toLowerCase().includes(searchKey) 
  )
  this.setState({bookings:result})
}


handleSearchBookingQuery =(e)=>{

  const searchKey = e.currentTarget.value;
  axios.get("http://localhost:8015/booking").then(res=>{

    if(res.data.success){
     this.filterData(res.data.existingBookings,searchKey)
        
    }
    });

}


  render() {
    return (

      <div className="tgdb" >
        <div className="hero-image">
                <div className="bg_banner"></div> &nbsp;
                <div className="hero-text">
                    <h1 ><center>Tour Guide Dashboard</center> <br/></h1>                       
                </div>

                
            </div>

        <br/>
      

        <table className= "table table-bordered" >
          <thead className="thead-bg-dark">
            <tr>
            <th scope="col">#</th>
            <th scope="col"><b>Customer Name</b></th>
            <th scope="col"><b>Customer Email</b></th>
            <th scope="col"><b>Mobile No</b></th>
            <th scope="col"><b>Tour Option</b></th>
            <th scope="col"><b>Date</b></th>
            <th scope="col"><b>Time</b></th>
            <th scope="col"><b>Tour Guide</b> </th>
            <th scope="col">Action</th>
            
            </tr>
          </thead>
          <tbody>
            {this.state.bookings.map((booking,index)=>(

              <tr key ={index}>
                <th scope="row">
                  <a href={`booking/${booking._id}`} style={{textDecoration:false}}>
                  {index+1}
                  </a>
                </th>
                <td>{booking.CustomerName}</td>
                <td>{booking.CustomerEmail}</td>
                <td>{booking.MobileNumber}</td>
                <td>{booking.TourOption}</td>
                <td>{booking.Date}</td>
                <td>{booking.Time} </td>
                <td>{booking.TourGuideName}</td>
                <td>
                  
                  <a className="btn btn-warning" href={`/edit/${booking._id}`}>
                     Edit  &nbsp;
                  </a>
                  &nbsp;  &nbsp; &nbsp;
                  <a className="btn btn-danger" hred="" onClick={() =>this.onDelete(booking._id)}>
                    <i className="far fa-trash-alt"></i> &nbsp; Delete
                  </a>
                  
                </td>

              </tr>

            ))}





          </tbody>



        </table>

        
        
        
        <button className ="btn btn-success"><a href="/add" style={{textDecoration:'none' ,color:'white' }}>  Add new Tour Booking </a></button>


       

     
      </div>
    )
  }
}

export default TourGuideDashboard
