import React, { Component } from 'react'
import axios from 'axios'
import { FormErrors } from './FormErrors';



export default class CreatePost extends Component {

constructor(props){
    super(props);
    this.state={

        userName:"",
        firstName:"",
        eID:"",
        lastName:"",
        email:"",
        address:"",
        employeeType:"",
        DOB:"",
        salary:"",

        formErrors: {userName: '', firstName:''},
            userNameValid: false,
            firstNameValid: false,
            formvalid: false
    }
}
handleInputChange = (e)=>{
    const {name,value} = e.target;
    this.setState({
        ...this.state,
        [name]:value
    },() => { this.validateField(name, value) }
);
}

    onSubmit = (e) =>{
        e.preventDefault();

        const {userName,firstName,eID,lastName,email,address,employeeType,DOB,salary} = this.state;



        const data={

            userName:userName,
            firstName:firstName,
            eID:eID,
            lastName:lastName,
            email:email,
            address:address,
            employeeType:employeeType,
            DOB:DOB,
            salary:salary
        }

        console.log(data)

        axios.post("http://localhost:8015/post/save",data).then((res)=>{
            if(res.data.success){
                this.setState(
                {
                  username:"",
                  firstName:"",
                  eID:"",
                  lastName:"",
                  email:"",
                  address:"",
                  employeeType:"",
                  DOB:"",
                  salary:""

                }
                )
            }
        })

    }










    validateField(fieldName, value) {
        let fieldValidationErrors = this.state.formErrors;
        let userNameValid = this.state.emailValid;
        let firstNameValid = this.state.passwordValid;

        switch(fieldName) {
          case 'email':
            userNameValid = value.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{2,})$/i);
            fieldValidationErrors.email = emailValid ? '' : ' is invalid';
            break;
          case 'password':
            passwordValid = value.length >= 6;
            fieldValidationErrors.password = passwordValid ? '': ' is too short';
            break;
          default:
            break;
        }
        this.setState({formErrors: fieldValidationErrors,
                        emailValid: emailValid,
                        passwordValid: passwordValid
                      }, this.validateForm);
      }

      validateForm() {
        this.setState({formValid: this.state.emailValid && this.state.passwordValid});
      }

      errorClass(error) {
        return(error.length === 0 ? '' : 'has-error');
      }












    render() {
        return (
            <div className="col-md-8 mt-4 mx-auto">
                <h1 className="h3 mb-3 font-weight-normal" style={{fontFamily:'Papyrus, fantasy'}}>Create new post</h1>
                <FormErrors formErrors={this.state.formErrors} className="FormError"/>
                <form className="needs-validation" noValidate>
                    <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >Username</label>
                    <input type="text"
                    className="form-control"
                    name="eID"
                    placeholder=""
                    value={this.state.eID}
                    onChange={this.handleInputChange}/>
                    </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >FirstName</label>
                <input type="text"
                className="form-control"
                name="userName"
                placeholder=""
                value={this.state.userName}
                onChange={this.handleInputChange}/></div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >eID</label>
                <input type="text"
                className="form-control"
                name="firstName"
                placeholder=""
                value={this.state.firstName}
                onChange={this.handleInputChange}/></div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >LastName</label>
                <input type="text"
                className="form-control"
                name="lastName"
                placeholder=""
                value={this.state.lastName}
                onChange={this.handleInputChange}/></div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >E-mail</label>
                <input type="text"
                className="form-control"
                name="email"
                placeholder=""
                value={this.state.email}
                onChange={this.handleInputChange}/></div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >Address</label>
                <input type="text"
                className="form-control"
                name="address"
                placeholder=""
                value={this.state.address}
                onChange={this.handleInputChange}/></div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >Employee Type</label>
                <input type="text"
                className="form-control"
                name="employeeType"
                placeholder=""
                value={this.state.employeeType}
                onChange={this.handleInputChange}/></div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >DOB</label>
                <input type="text"
                className="form-control"
                name="DOB"
                placeholder=""
                value={this.state.DOB}
                onChange={this.handleInputChange}/></div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                <label style={{marginBottom:'5px',fontFamily:'Papyrus, fantasy'}} >Salary</label>
                <input type="text"
                className="form-control"
                name="salary"
                placeholder=""
                value={this.state.salary}
                onChange={this.handleInputChange}/></div>


                    <button className="btn btn-success" type="submit" style={{marginTop:'15px'}} onClick={this.onSubmit}>
                        <i className="far fa-check-square"></i>
                        &nbsp; Save
                    </button>

                    <br/><br/><br/><br/><br/>


                </form>

            </div>
        )
    }
}
